package string;

public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		StringDemo s1=new StringDemo();
		System.out.println("printing s1:"+s1);
		String s2=new String("pune is IT hub");
		System.out.println("printing s2:"+s2);
		System.out.println("s2:"+s2.toString());
		System.out.println("s2:"+s2.length());
		System.out.println("s2:"+s2.charAt(8));
		System.out.println("s2:"+s2.substring(5));
		System.out.println("s2:"+s2.substring(3,8));
		String s3="core";
	String s4="java basics";
	System.out.println("concate:"+s3.concat(s4));
	//String
	
	}

}
